import React, { Children, useState } from 'react';
import {
  MenuFoldOutlined,
  MenuUnfoldOutlined,
  UploadOutlined,
  UserAddOutlined,
  TeamOutlined,
  HomeOutlined,
} from '@ant-design/icons';
import { Layout, Menu, Button, Space, theme } from 'antd';
import { useNavigate } from 'react-router-dom';
import AppHeader from '../AppHeader/header';
import AppFooter from '../AppFooter/footer';
import PageContent from '../PageContent/PageContent';


const {Header, Content, Footer,Sider,} = Layout

const SideMenu = () => {
  const navigate = useNavigate();
  const [collapsed, setCollapsed] = useState(false);
  const {
    token: { colorBgContainer },
  } = theme.useToken();

  return (
    <Layout>
      <Sider style={{ 
        textAlign: 'center',
        lineHeight: '120px',
        }}
      
        trigger={null} collapsible collapsed={collapsed}>
        <div className="menuitems">
          <Menu
            theme="dark"
            mode="inline"
            defaultSelectedKeys={['/da']}
            onClick={(items) => {navigate(items.key);}} //item.key
            
            items={[
              {
                key:'/dasboard',
                icon:<HomeOutlined />,
                label: 'Dashboard',

              },
              {
                key: '/employeeform',
                icon: <UserAddOutlined />,
                label: 'EmployeeForm',
              },
              {
                key: '/employeelist',
                icon: <TeamOutlined />,
                label: 'EmployeeForm',
              },
              {
                key: '3',
                icon: <UploadOutlined />,
                label: 'nav 3',
              },
            ]}
          />
        </div>
      </Sider>
      <Layout>
          <Header style={{
            padding: 0,
            background: colorBgContainer,
          }}> <Space>
                <Button
                  type="text"
                  icon={collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
                  onClick={() => setCollapsed(!collapsed)}
                  style={{
                    fontSize: '16px',
                    width: 64,
                    height: 64,
                  }} />             
                  <AppHeader />  
                </Space>                                                
          </Header>
          
          <Content style={{
            margin: '0 16px 0',
          }}>
              <PageContent  style={{
              padding: 24,
              minHeight: 360,
              background: colorBgContainer,             
              }}>
              </PageContent>
              <div
                style={{
                  padding: 24,
                  minHeight: 500,
                  background: colorBgContainer,
                }}
              ></div>

          </Content>
          
          <Footer> 
              <AppFooter></AppFooter>    
          </Footer>
      </Layout>
      
    </Layout>
  );
};
export default SideMenu;
